mapper.select:
SELECT
    AUTH_KEY, EMAIL, ROLE
FROM AUTHENTICATION_TB
WHERE AUTHENTICATION_TB.AUTH_KEY = #{authKey}
AND AUTHENTICATION_TB.EMAIL = #{email}
AND AUTHENTICATION_TB.ROLE = #{role}

mapper.delete:
DELETE FROM AUTHENTICATION_TB
WHERE AUTHENTICATION_TB.EMAIL = #{email}
AND AUTHENTICATION_TB.ROLE = #{role}
